let color="rgba(49, 140, 231, 0.2)";const red=document.querySelector(".red"),blue=document.querySelector(".blue"),green=document.querySelector(".green");function colorChart(e){for(let r=0;r<myChart.data.datasets[0].backgroundColor.length;r++)myChart.data.datasets[0].backgroundColor[r]=e,console.log(myChart.data.datasets[0].backgroundColor[r]),myChart.update();for(let r=0;r<myChart.data.datasets[0].borderColor.length;r++)myChart.data.datasets[0].borderColor[r]=e,console.log(myChart.data.datasets[0].borderColor[r]),myChart.update()}red.addEventListener("click",e=>{colorChart("red")}),blue.addEventListener("click",e=>{colorChart("blue")}),green.addEventListener("click",e=>{colorChart("green")});var ctx=document.getElementById("myChart").getContext("2d"),myChart=new Chart(ctx,{type:"bar",data:{labels:["Иван Петрович","Максим Иванович","Денис Максимович"],datasets:[{label:"Баланс ₽",data:[1e3,8e3,3e3],backgroundColor:["rgba(255, 99, 132, 0.2)","rgba(54, 162, 235, 0.2)","rgba(255, 206, 86, 0.2)"],borderColor:[color,color,color],borderWidth:1}]},options:{scales:{y:{beginAtZero:!0}}}});